import axios from "axios";
import Url from "../../../../store/Api";
import { mapGetters } from "vuex";
import BreadCrumb from "../../../../components/Intermediaire/BreadCrumb.vue";
import CardAjouter from "../../../../components/Intermediaire/CardAjouter.vue";
import PaginationVue from "../../../../components/Intermediaire/pagination/PaginationVue.vue";
export default {
  components: {
    BreadCrumb,
    CardAjouter,
    PaginationVue
  },

  data() {
    return {
      openModifier: false,
      open: false,
      ParpageTransporteur: "10",
      rechercheTransporteur: "",
      //data a ajouter
      nom: "",
      prenom: "",
      password: "",
      email: "",
      cpassword: "",
      imageFile: null,
      idTransporteur: "",
      iduser: "",
      index: "",
      societe: "",
      tel: "",
      societe: "" // timeout: false,

    };
  },

  computed: { ...mapGetters(["ListeTransporteurs", "ListeSocietesAll", "currentTransporteur", "perPageTransporteur", "parPageTransporteur", "totalTransporteur"])
  },

  mounted() {
    this.$store.dispatch("Get_Transporteur");
    this.$store.dispatch("Get_Societe_All");
  },

  methods: {
    GetSociete(id) {
      return this.ListeSocietesAll.filter(el => el.idSociete == id)[0].nom;
    },

    Ajouter() {
      this.open = true;
      console.log(this.open);
    },

    Close(E) {
      this.open = E;

      if (this.openModifier) {
        this.openModifier = E;
        this.nom = "";
        this.prenom = "";
        this.email = "";
        this.tel = "";
        this.cpassword = "";
        this.password = "";
        this.imageFile = null;
      }
    },

    Supprimer(transporteur) {
      this.$swal({
        title: "Supprimer ?",
        text: "",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Oui, Supprimer",
        cancelButtonText: "Annuler"
      }).then(result => {
        if (result.isConfirmed) {
          this.$store.dispatch("Delete_Transporteur", transporteur);
          this.$swal({
            position: "top-end",
            icon: "success",
            toast: true,
            title: "Supprimer",
            showConfirmButton: false,
            timer: 2000
          });
        }
      });
    },

    ChangePage(NumPage) {
      this.$store.dispatch("Get_NoveauTransporteur", NumPage);
    },

    //
    SaveTransporteur() {
      if (this.password == this.cpassword) {
        if (this.nom != "" && this.prenom != "" && this.email != "" && this.password != "" && this.societe != "") {
          let user = new FormData();
          user.append("nom", this.nom);
          user.append("prenom", this.prenom);
          user.append("email", this.email);
          user.append("motdepasse", this.password);
          user.append("image", "intermediaire.png");
          user.append("Tel", this.tel);
          user.append("societe", this.societe);
          user.append("ImageFile", this.imageFile);
          user.append("ImageSrc", ""); //  user.append("Societe", this.societe);

          axios.post(Url + "users", user, {
            headers: {
              Authorization: "Bearer " + localStorage.getItem("token")
            }
          }).then(res => {
            // let per = {
            //   Idrole: this.idRole,
            //   personnelle: res.data,
            //   IdRoleNavigation: this.GetRole(this.idRole),
            // };
            this.$store.dispatch("Ajouter_Transporteur", res.data);
            this.Close(false);
            this.$swal({
              position: "top-end",
              icon: "success",
              toast: true,
              title: "Transporteur Ajouter",
              showConfirmButton: false,
              timer: 2000
            });
            this.nom = "";
            this.prenom = "";
            this.email = "";
            this.password = "";
            this.cpassword = "";
            this.tel = "";
            this.imageFile = null;
          }).catch(() => {
            this.$swal({
              position: "top-end",
              icon: "error",
              toast: true,
              title: "Email utilisé",
              showConfirmButton: false,
              timer: 2000
            });
          });
        } else {
          //this.startAlert();
          this.$swal({
            position: "top-end",
            icon: "error",
            toast: true,
            title: "Il faut remplir tous les champs",
            showConfirmButton: false,
            timer: 2000
          });
          this.open = false;
        }
      } else {
        this.$swal({
          position: "top-end",
          icon: "error",
          toast: true,
          title: "Les mots de passe diferent",
          showConfirmButton: false,
          timer: 2000
        });
      }
    },

    Modifier(transporteur, index) {
      this.openModifier = true;
      this.nom = transporteur.idUserNavigation.nom;
      this.prenom = transporteur.idUserNavigation.prenom;
      this.email = transporteur.idUserNavigation.email; // this.password = transporteur.idUserNavigation.motdepasse;

      this.tel = transporteur.idUserNavigation.tel;
      this.societe = transporteur.idUserNavigation.societe;
      this.iduser = transporteur.idUser;
      this.idTransporteur = transporteur.idTransporteur;
      this.index = index; // console.log(personnelle.idUserNavigation);
    },

    SaveModifier() {
      if (this.password == this.cpassword) {
        if (this.iduser != "" && this.nom != "" && this.prenom != "" && this.email != "" && this.password != "") {
          let user = new FormData();
          user.append("idUser", this.iduser);
          user.append("nom", this.nom);
          user.append("prenom", this.prenom);
          user.append("email", this.email);
          user.append("motdepasse", this.password);
          user.append("image", "");
          user.append("Tel", this.tel);
          user.append("societe", this.societe);
          user.append("ImageFile", this.imageFile);
          user.append("ImageSrc", "");
          axios.put(Url + "users/" + this.iduser, user, {
            headers: {
              Authorization: "Bearer " + localStorage.getItem("token")
            }
          }).then(res => {
            let transporteur = {
              idTransporteur: this.idTransporteur,
              idUser: this.iduser,
              imageSrc: res.data.imageSrc,
              idUserNavigation: res.data,
              index: this.index
            };
            this.$store.dispatch("Modifier_Transporteur", transporteur);
            this.Close(false);
            this.$swal({
              position: "top-end",
              icon: "success",
              toast: true,
              title: "Transporteur Modifié",
              showConfirmButton: false,
              timer: 2000
            });
            this.nom = "";
            this.prenom = "";
            this.email = "";
            this.password = "";
            this.cpassword = "";
            this.tel = "";
            this.imageFile = null;
          }).catch(() => {
            this.$swal({
              position: "top-end",
              icon: "error",
              toast: true,
              title: "Email utilisé",
              showConfirmButton: false,
              timer: 2000
            });
          });
        } else {
          //this.startAlert();
          this.$swal({
            position: "top-end",
            icon: "error",
            toast: true,
            title: "Il faut remplir tous les champs",
            showConfirmButton: false,
            timer: 2000
          });
          this.Close(false);
        }
      } else {
        this.$swal({
          position: "top-end",
          icon: "error",
          toast: true,
          title: "Les mots de passe diferent",
          showConfirmButton: false,
          timer: 2000
        });
      }
    },

    // ajouter image
    FileSelected(event) {
      this.imageFile = event.target.files[0];
      console.log(this.imageFile);
    },

    Active(user) {
      user.active = 1;
      console.log(user);
      this.$store.dispatch("Active_Compte", user);
      this.$swal({
        position: "top-end",
        icon: "success",
        toast: true,
        title: "Compte Activée",
        showConfirmButton: false,
        timer: 2000
      });
    },

    Desactive(user) {
      user.active = null;
      console.log(user);
      this.$store.dispatch("Active_Compte", user);
      this.$swal({
        position: "top-end",
        icon: "info",
        toast: true,
        title: "Compte Desactivée",
        showConfirmButton: false,
        timer: 2000
      });
    }

  },
  watch: {
    ParpageTransporteur() {
      this.$store.dispatch("Changer_ParpageTransporteur", this.ParpageTransporteur);
    },

    rechercheTransporteur() {
      this.$store.dispatch("Chercher_Transporteur", this.rechercheTransporteur);
    }

  }
};
